import React, {useEffect, useState} from "react";
import { bindActionCreators } from "redux";
import { connect } from "react-redux";
import { GetWeatherDetails } from "../redux/Actions";
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import moment from "moment";


const Weather = (props) => {

  const [searchInput, setSearchInput] = useState("");
  const { GetWeatherDetails } = props?.action;
  const { data, success } = props.weatherData;
  const { weather, sys, name, main } = data;

  useEffect(() => {
    GetWeatherDetails();
  }, [])


  const handleSubmit = e => {
    e.preventDefault();
    if (searchInput) GetWeatherDetails(searchInput);
    setSearchInput("");
  };
  const handleOnChange = e => {
    setSearchInput(e.target.value);
  };


  return (
    <>
      <div className="container">
        <span className="heading">Weather Finder</span>
        <span className="location">
            {success ? name : null}
          <small>({success ? sys.country : null})</small>
          </span>
        <form className="search-form" onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Search Weather by City"
            value={searchInput}
            onChange={e => handleOnChange(e)}
          />
          <button>Find</button>
        </form>
        <div className="info">
          <div className="sub-heading">
            Weather Forecast {success ? moment().format("MMM DD YYYY") : null}
          </div>

          <div className="forecast-info">
            <div className="forecast-icon">
              {success ? (
                <img
                  src={`http://openweathermap.org/img/wn/${weather[0].icon}@2x.png`}
                  alt=""
                />
              ) : null}
            </div>
            <div className="forecast-value">
              <div className="degrees">
                <span className="degrees-count">
                  {success ? main.temp : null}
                </span>
                C
              </div>
              <span className="weather-condition">
                {success ? weather[0].main : null}
              </span>
            </div>
          </div>
          <div className="additional-info">
            <ul className="list">
              <li>
                <b style={{color: 'red'}}>Feels Like:</b> {success ? main.feels_like : null}
              </li>
              <li>
                <b style={{color: 'red'}}>Min Temp:</b> {success ? main.temp_min : null}
              </li>
              <li>
                <b style={{color: 'red'}}>Max Temp:</b> {success ? main.temp_max : null}
              </li>
              <li>
                <b style={{color: 'red'}}>Pressure:</b> {success ? main.pressure : null}
              </li>
              <li>
                <b style={{color: 'red'}}>Humidity:</b> {success ? main.humidity : null}
              </li>
            </ul>
          </div>
        </div>
      </div>
      <ToastContainer />
    </>
  );
}

const mapStateToProps = state => ({
  weatherData: state
});

const mapDispatchToProps = dispatch => ({
  action: bindActionCreators({ GetWeatherDetails }, dispatch)
});
export default connect(mapStateToProps, mapDispatchToProps)(Weather);
